const cleanup = $node["Convertir a JSON3"].json;

// Generar resumen de sesi√≥n
const sessionId = post.session_id;
let privilegeEscalation = 'No intentado';
let credentialsFound = 0;
let lateralMovement = 'No intentado';
let evasionSuccess = 'Desconocido';
let cleanupSuccess = 'Desconocido';

// Verificar escalada de privilegios
if (post.privilege_escalation && post.privilege_escalation.success) {
  privilegeEscalation = 'Exitoso';
} else if (post.privilege_escalation) {
  privilegeEscalation = 'Fallido';
}

// Contar credenciales encontradas
if (post.credentials && post.credentials.credentials_found) {
  credentialsFound = post.credentials.credentials_found.length;
}

// Verificar movimiento lateral
if (post.lateral_movement && post.lateral_movement.success) {
  lateralMovement = 'Exitoso';
} else if (post.lateral_movement) {
  lateralMovement = 'Fallido';
}

// Verificar √©xito de evasi√≥n
if (evasion.success) {
  evasionSuccess = 'Exitoso';
} else {
  evasionSuccess = 'Fallido';
}

// Verificar √©xito de limpieza
if (cleanup.success) {
  cleanupSuccess = 'Exitoso';
} else {
  cleanupSuccess = 'Fallido';
}

// Generar reporte
return {
  target: exploit.target,
  timestamp: new Date().toISOString(),
  session_id: sessionId,
  exploitation: {
    success: exploit.successful_exploits > 0,
    exploits_attempted: exploit.exploits ? exploit.exploits.length : 0,
    successful_exploits: exploit.successful_exploits
